mapper.select:
	    SELECT e.ITINDUSTRY_WORK_YEAR,e.EMPLOYEE_ID,e.HSBC_STAFF_ID,e.E_HR,e.LOB,e.STAFF_NAME,e.LN,d.CS_SUBDEPT_NAME,e.RESOURCE_STATUS,e.RM_USER_ID,u.NICKNAME,e.ENGAGEMENT_TYPE
        FROM t_employee AS e
        LEFT JOIN t_cs_dept AS d
        ON e.CS_SUBDEPT_ID = d.CS_SUBDEPT_ID
        LEFT JOIN T_USER AS u
        ON e.RM_USER_ID = u.USER_ID
        WHERE 1 = 1
        
			
				AND e.HSBC_STAFF_ID = #{hsbcStaffId}
			
			
				AND e.E_HR = #{eHr}
			
			
				AND e.LOB = #{lob}
			
			
				AND d.CS_DEPT_NAME = #{csDeptName}
			
			
				AND d.CS_SUBDEPT_NAME = #{csSubDeptName}
			
			
				AND d.CS_BU_NAME = #{csbuName}
			
			
				AND e.RESOURCE_STATUS = #{resourceStatus}
			
			
				AND e.STAFF_NAME LIKE '%${staffName}%' 
			
			
				AND e.RM_USER_ID = #{rmUserId}
			
			
				AND e.ENGAGEMENT_TYPE = #{engagementType}
			
			
		ORDER BY u.NICKNAME,e.ROLE
    LIMIT #{currentPage},#{pageRecordsNum};

	    SELECT count(0)
        FROM t_employee AS e
        LEFT JOIN t_cs_dept AS d
        ON e.CS_SUBDEPT_ID = d.CS_SUBDEPT_ID
        WHERE 1 = 1
        
			
				AND e.HSBC_STAFF_ID = #{hsbcStaffId}
			
			
				AND e.E_HR = #{eHr}
			
			
				AND e.LOB = #{lob}
			
			
				AND d.CS_DEPT_NAME = #{csDeptName}
			
			
				AND d.CS_SUBDEPT_NAME = #{csSubDeptName}
			
			
				AND d.CS_BU_NAME = #{csbuName}
			
			
				AND e.RESOURCE_STATUS = #{resourceStatus}
			
			
				AND e.STAFF_NAME = #{staffName}
			
		
			
				AND e.RM_USER_ID = #{rmUserId}
			
			
				AND e.ENGAGEMENT_TYPE = #{engagementType}

SELECT DISTINCT z1.EMPLOYEE_ID, z1.CS_SUBDEPT_ID, z1.HSBC_STAFF_ID, z1.E_HR,z1.LOB, z1.STAFF_NAME, z1.LN, z1.CS_SUBDEPT_NAME, z1.CS_BU_NAME, z1.RESOURCE_STATUS, z1.RM_USER_ID, z1.NICKNAME, z1.ENGAGEMENT_TYPE from( SELECT e.EMPLOYEE_ID, e.CS_SUBDEPT_ID, e.HSBC_STAFF_ID, e.E_HR,e.LOB, e.STAFF_NAME, e.LN, d.CS_SUBDEPT_NAME, d.CS_BU_NAME, e.RESOURCE_STATUS, e.RM_USER_ID, u.NICKNAME, e.ENGAGEMENT_TYPE FROM t_employee AS e LEFT JOIN t_cs_dept AS d ON e.CS_SUBDEPT_ID = d.CS_SUBDEPT_ID LEFT JOIN T_USER AS u ON e.RM_USER_ID = u.USER_ID) z1 INNER JOIN t_employee_log z2 on z1.EMPLOYEE_ID=z2.EMPLOYEE_ID WHERE 1 = 1
AND z1.HSBC_STAFF_ID = #{hsbcStaffId}
AND z1.E_HR = #{eHr}
AND z1.LOB = #{lob}
AND z1.STAFF_NAME LIKE '%${staffName}%'
AND z1.RM_USER_ID = #{rmUserId}
AND z1.CS_BU_NAME = #{csbuName}
AND z1.CS_BU_NAME in
#{item}
AND z1.CS_SUBDEPT_NAME = #{csSubDeptName}
AND z1.CS_SUBDEPT_ID in
#{item}
LIMIT #{currentPage},#{pageRecordsNum};

SELECT COUNT(DISTINCT z1.EMPLOYEE_ID) from( SELECT e.EMPLOYEE_ID, e.CS_SUBDEPT_ID, e.HSBC_STAFF_ID, e.E_HR,e.LOB, e.STAFF_NAME, e.LN, d.CS_SUBDEPT_NAME, d.CS_BU_NAME, e.RESOURCE_STATUS, e.RM_USER_ID, u.NICKNAME, e.ENGAGEMENT_TYPE FROM t_employee AS e LEFT JOIN t_cs_dept AS d ON e.CS_SUBDEPT_ID = d.CS_SUBDEPT_ID LEFT JOIN T_USER AS u ON e.RM_USER_ID = u.USER_ID) z1 INNER JOIN t_employee_log z2 on z1.EMPLOYEE_ID=z2.EMPLOYEE_ID WHERE 1 = 1
AND z1.HSBC_STAFF_ID = #{hsbcStaffId}
AND z1.E_HR = #{eHr}
AND z1.LOB = #{lob}
AND z1.STAFF_NAME LIKE '%${staffName}%'
AND z1.RM_USER_ID = #{rmUserId}
AND z1.CS_BU_NAME = #{csbuName}
AND z1.CS_BU_NAME in
